  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'amf_intro',
	title: 'What is AMF?'
};
export const metadata = {
  "unversionedId": "AMF/amf_intro",
  "id": "AMF/amf_intro",
  "isDocsHomePage": false,
  "title": "What is AMF?",
  "description": "AMF (AML Modeling Framework) is an open-source library capable of parsing and validating  AML metadata documents.",
  "source": "@site/../docs/AMF/what_is_amf.mdx",
  "slug": "/AMF/amf_intro",
  "permalink": "/docs/AMF/amf_intro",
  "version": "current",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar": "docs",
  "next": {
    "title": "AMF project configuration",
    "permalink": "/docs/AMF/using-amf/amf_setup"
  }
};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const toc = [{
  value: 'Vision',
  id: 'vision',
  children: []
}, {
  value: 'Goals',
  id: 'goals',
  children: []
}, {
  value: 'Native support',
  id: 'native-support',
  children: [{
    value: 'Features',
    id: 'features',
    children: []
  }, {
    value: 'Artifacts',
    id: 'artifacts',
    children: []
  }]
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p><a parentName="p" {...{
        "href": "https://github.com/aml-org/amf"
      }}>{`AMF`}</a>{` (AML Modeling Framework) is an open-source library capable of parsing and validating  AML metadata documents.
AMF is fully written in Scala, and thanks to `}<a parentName="p" {...{
        "href": "https://www.scala-js.org/"
      }}>{`Scala.js`}</a>{` it can be executed in the JVM, Node.js or the browser.
AMF can be used as a standalone command-line tool or as a library in Scala, Java, web or node applications.`}</p>
    <p>{`AMF source code is publicly available under the `}<a parentName="p" {...{
        "href": "https://www.apache.org/licenses/LICENSE-2.0"
      }}>{`Apache 2.0 License`}</a>{` in the `}<a parentName="p" {...{
        "href": "https://github.com/aml-org/amf"
      }}>{`AMF Github repository`}</a>{`.`}</p>
    <p>{`The modular design of AMF facilitates creating plugins capable of parsing other metadata syntaxes not defined by AML.
For example, the most used AMF module, the web-api module, is capable of parsing `}<a parentName="p" {...{
        "href": "https://raml.org/"
      }}>{`RAML`}</a>{`, `}<a parentName="p" {...{
        "href": "https://github.com/OAI/OpenAPI-Specification"
      }}>{`OAS`}</a>{` (formerly Swagger) and `}<a parentName="p" {...{
        "href": "https://github.com/asyncapi/asyncapi"
      }}>{`AsyncAPI`}</a>{` API specification languages.
Furthermore, AMF can also validate APIs, and even emit them in different API languages.`}</p>
    <h2 {...{
      "id": "vision"
    }}>{`Vision`}</h2>
    <p><a parentName="p" {...{
        "href": "https://a.ml/aml-spec"
      }}>{`AML`}</a>{` allows users to formally describe different kinds of models, whether syntactic models (i.e. language/specification specific) or semantic models (i.e. domain/industry specific).
AMF can parse and generate descriptions of those models as sets of modular documents and store those connected descriptions into a single unified data graph.`}</p>
    <h2 {...{
      "id": "goals"
    }}>{`Goals`}</h2>
    <ul>
      <li parentName="ul">{`Support for multiple formats with a unified programming interface and model`}</li>
      <li parentName="ul">{`Support for both syntactic and semantic model layers`}</li>
      <li parentName="ul">{`Support for validation at both syntactic and semantic model layers`}</li>
      <li parentName="ul">{`Produce formal specifications of any (specification) language`}</li>
      <li parentName="ul">{`Extensible, single syntactic model for multiple semantic models`}</li>
      <li parentName="ul">{`Ability to create consistent parsing behaviors across different syntactic models`}</li>
      <li parentName="ul">{`Support for bi-directional transformation and export`}</li>
      <li parentName="ul">{`Support for custom (parsing/resolution/generation) pipelines`}</li>
    </ul>
    <h2 {...{
      "id": "native-support"
    }}>{`Native support`}</h2>
    <p>{`AMF natively supports the following formats:`}</p>
    <ul>
      <li parentName="ul">{`YAML`}</li>
      <li parentName="ul">{`JSON`}</li>
    </ul>
    <p>{`the following semantic models:`}</p>
    <ul>
      <li parentName="ul">{`WebApi (or "Web APIs" as in "APIs accessible over the network")`}</li>
    </ul>
    <p>{`and the following syntactic models:`}</p>
    <ul>
      <li parentName="ul">{`JSON-LD "AMF model"`}</li>
      <li parentName="ul">{`RAML 0.8 / 1.0 (mapped to "WebApi")`}</li>
      <li parentName="ul">{`OpenAPI (OAS) 2.0 / 3.0 (mapped to "WebApi")`}</li>
      <li parentName="ul">{`AsyncAPI 2.0 (mapped to "AsyncApi")`}</li>
    </ul>
    <p>{`The models above and any other models may be extended and supported via custom `}<a parentName="p" {...{
        "href": "https://a.ml/aml-spec"
      }}>{`AML-defined models`}</a>{`.
Other formats and models that cannot be expressed with AML may also be supported via plugins.`}</p>
    <h3 {...{
      "id": "features"
    }}>{`Features`}</h3>
    <p>{`AMF has many features such as:`}</p>
    <ul>
      <li parentName="ul">{`Parsing`}</li>
      <li parentName="ul">{`Resolution`}
        <ul parentName="li">
          <li parentName="ul">{`Stages & Pipelines`}</li>
        </ul>
      </li>
      <li parentName="ul">{`Validation`}
        <ul parentName="li">
          <li parentName="ul">{`Custom Model Constraints (WebApi)`}</li>
          <li parentName="ul">{`SHACL (Vocabularies & Custom validations)`}</li>
        </ul>
      </li>
      <li parentName="ul">{`Rendering`}</li>
    </ul>
    <h3 {...{
      "id": "artifacts"
    }}>{`Artifacts`}</h3>
    <p>{`Artifacts to use AMF can be obtained from:`}</p>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://repository-master.mulesoft.org/nexus/content/repositories/releases/com/github/amlorg/"
        }}>{`Maven`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://www.npmjs.com/package/amf-client-js"
        }}>{`NPM`}</a></li>
    </ul>
    <p>{`In the next section you can learn how to quickly get started using AMF.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
